import React, { useState } from "react";

function AddTransactionForm() {
  // State for form inputs
  const [date, setDate] = useState(null)
  const [description, setDescription] = useState(null)
  const [category, setCategory] = useState(null)
  const [amount, setAmount] = useState(null)

  // State for form data
  const [formData, setFormData] = useState({
    date: date,
    description: description,
    category: category,
    amount: amount,
  })

  console.log(formData)

  return (
    <div className="ui segment">
      <form className="ui form" onSubmit={setFormData}>
        <div className="inline fields">
          <input onChange={setDate} type="date" name="date" />
          <input onChange={setDescription} type="text" name="description" placeholder="Description" />
          <input onChange={setCategory} type="text" name="category" placeholder="Category" />
          <input onChange={setAmount} type="number" name="amount" placeholder="Amount" step="0.01" />
        </div>
        <button className="ui button" type="submit">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
